    public class LocalSign
    {
        private ISealService sealService;

        private ILocalSignService localSignService;

        public LocalSign(SDKClient sdkClient)
        {
            sealService = new SealServiceImpl(sdkClient);
            localSignService = new LocalSignServiceImpl(sdkClient);
        }


        public void Process()
        {
            try
            {
                //平台签署
                SignbyPlatform();

                //公司签署
                //SignbyCompany();

                //个人签署
                //SignbyPersonal();

                //完成并封存合同
                //Complete();

                //校验合同有效性
                //Verify();
            }
            catch (Exception e)
            {
                Console.WriteLine("本地签过程中出错，错误原因：{0}", e.Message);
            }
            Console.ReadKey();//防止闪退 
        }

        /**
         *  平台签署 
         */
        public void SignbyPlatform()
        {
            Stream file = new FileStream("C://Users//Administrator//Desktop//测试.pdf", FileMode.Open);
            Stream signfile = new FileStream("C://Users//Administrator//Desktop//RRDENSE.pdf", FileMode.Create);
            //平台签署本地合同(无签名外观)
            //localSignService.Sign(file, ref signedFile);
            //平台签署本地合同(有签名外观)
            Stamper stamper = new Stamper(1, 0.5f, 0.5f);
            localSignService.Sign(file, ref signfile, "2279225369938624512", stamper);
            Console.WriteLine("平台签署已完成");
            file.Close();
        }

        /**
         *  公司签署 
         */
        public void SignbyCompany()
        {
            Stream file = new FileStream("C://Users//Administrator//Desktop//RRDENSE135.pdf", FileMode.Open);
            Company company = new Company("本地签测试公司");
            company.registerNo = "16600000000";
            //公司用户签署本地合同(无签名外观)
            //localSignService.Sign(file, ref signedFile, company);
            //公司用户签署本地合同(有签名外观)
            Stamper stamper = new Stamper(1, 0.5f, 0.5f);
            string sealData = "";
            localSignService.Sign(file, ref file, company, sealData, stamper);
            Console.WriteLine("公司签署已完成");
            file.Close();
        }


        /**
         *  个人签署 
         */
        public void SignbyPersonal()
        {
            Stream file = new FileStream("C://Users//Administrator//Desktop//RRDENSE135.pdf", FileMode.Open);
            Person person = new Person("本地签测试人");
            person.mobile = "16600000000";
            //个人用户签署本地合同(无签名外观)
            //localSignService.Sign(file, ref signedFile, person);
            //个人用户签署本地合同(有签名外观)
            Stamper stamper = new Stamper(1, 0.5f, 0.5f);
            string sealData = "data:image/jpg;base64,";
            localSignService.Sign(file, ref file, person, sealData, stamper);
            Console.WriteLine("个人签署已完成");
            file.Close();
        }

        /**
         *  合同完成，并封存
         */
        public void Complete()
        {
            FileStream file = new FileStream("C://Users//Administrator//Desktop//平台签署带外观.pdf", FileMode.Open);
            Stream completeFile = new FileStream("C://Users//Administrator//Desktop//CompleteSigned.pdf", FileMode.CreateNew);
            localSignService.Complete(file, ref completeFile);
            Console.WriteLine("合同已完成并封存");
            file.Close();
        }


        /**
         *  校验合同有效性 
         */
        public void Verify()
        {
            FileStream file = new FileStream("C://Users//Administrator//Desktop//RRDENSE135.pdf", FileMode.Open);
            try
            {
                localSignService.Verify(file);
            }
            catch (Exception e)
            {
                Console.WriteLine("校验有效性为无效，无效原因：{0}", e.Message);
            }
            Console.WriteLine("校验合同有效性为：有效");
            file.Close();
        }
    }
}
